using UnityEngine;
using System.Collections;

public class UnityStar : MonoBehaviour 
{

	public Vector3 Position
	{
		get { return this.gameObject.transform.position; }
		set { this.gameObject.transform.position = value; }
	}
	
	public static UnityStar Instantiate(Vector3 pos)
	{
		int starVariantIndex = Random.Range (0,3);
		string starVariantName = "";
		switch (starVariantIndex)
		{
			case 0:
				starVariantName = "Yellow";
				break;
			case 1:
				starVariantName = "Red";
				break;
			case 2:
				starVariantName = "Blue";
				break;
			default:
				break;
		}
		var star = GameObject.Instantiate(Resources.Load(starVariantName + "Star"), pos, Quaternion.identity) as GameObject;
		star.transform.localScale = new Vector3(0.0025f,0.0025f,0.0025f);
		return star.GetComponent<UnityStar>();
	}
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    