using UnityEngine;
using UnityEngine.UI;
using System.Collections;

public class UnityText : MonoBehaviour 
{
	
	public string Text
	{
		get { return this.gameObject.GetComponent<Text>().text; }
		set { this.gameObject.GetComponent<Text>().text = value; }
	}

	public float Alpha
  {
    get { return this.gameObject.GetComponent<Text>().color.a; }
    set 
    {
      Color currentColor = this.gameObject.GetComponent<Text>().color;
      Color newColor = new Color(currentColor.r, currentColor.g, currentColor.b, value);
      this.gameObject.GetComponent<Text>().color = newColor;
    }
  }
	
	public static UnityText GetText(string name)
	{
    return GameObject.Find(name).GetComponent<UnityText>();
	}
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         